"use strict";
var _ = require("lodash");
var cipherNames = [
    "Amsco",
    "CaesarShift",
    "ColumnTransposition",
    "HillCipher",
    "Playfair",
    "SimpleSubstitution",
    "Vigenere"
];
function getCiphersExportStr(ciphers) {
    return ciphers.map(function (cipherName) { return ("import " + cipherName + " = require(\"./Ciphers/" + cipherName + "\");"); }).join("\n") + "\n\n"
        + ciphers.map(function (cipherName) { return ("export var " + _.lowerFirst(cipherName) + " = new " + cipherName + "();"); }).join("\n");
}
console.log(getCiphersExportStr(cipherNames));
